import React, { useState } from 'react';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  Grid,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Chip,
  FormControl,
  FormLabel,
  RadioGroup,
  FormControlLabel,
  Radio,
  Alert,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Tooltip,
  CircularProgress,
} from '@mui/material';
import { ExpandMore, Add, Remove, Upload, ContentCopy, Help, Link, AutoAwesome } from '@mui/icons-material';
import { ReelsContent, TextPosition, TextStyle } from '../types';

interface ContentStepProps {
  content: ReelsContent;
  textPosition: TextPosition;
  textStyle: TextStyle;
  onChange: (content: ReelsContent) => void;
  onTextPositionChange: (position: TextPosition) => void;
  onTextStyleChange: (style: TextStyle) => void;
  onNext: () => void;
}

const ContentStep: React.FC<ContentStepProps> = ({ content, textPosition, onChange, onTextPositionChange, onNext }) => {
  const [scriptCount, setScriptCount] = useState(2); // Í∏∞Î≥∏ 2Í∞ú ÎåÄÏÇ¨
  const [errors, setErrors] = useState<{ [key: string]: string }>({});
  const [jsonInput, setJsonInput] = useState('');
  const [jsonError, setJsonError] = useState('');
  const [showOverwriteDialog, setShowOverwriteDialog] = useState(false);
  const [parsedContent, setParsedContent] = useState<ReelsContent | null>(null);
  const [showJsonHelp, setShowJsonHelp] = useState(false);
  const [urlInput, setUrlInput] = useState('');
  const [isExtracting, setIsExtracting] = useState(false);
  const [extractError, setExtractError] = useState('');

  // ÌòÑÏû¨ ÏûëÏÑ±Îêú ÎåÄÏÇ¨ Í∞úÏàò Í≥ÑÏÇ∞
  const getFilledScriptCount = () => {
    let count = 0;
    for (let i = 1; i <= 8; i++) {
      const bodyKey = `body${i}` as keyof ReelsContent;
      if (content[bodyKey]?.trim()) {
        count++;
      }
    }
    return count;
  };

  const addScript = () => {
    if (scriptCount < 8) {
      setScriptCount(scriptCount + 1);
    }
  };

  const removeScript = () => {
    if (scriptCount > 1) {
      setScriptCount(scriptCount - 1);
      // Ï†úÍ±∞Îêú ÎåÄÏÇ¨ ÎÇ¥Ïö© Ï¥àÍ∏∞Ìôî
      const bodyKey = `body${scriptCount}` as keyof ReelsContent;
      onChange({ ...content, [bodyKey]: '' });
    }
  };

  const handleChange = (field: keyof ReelsContent, value: string) => {
    onChange({ ...content, [field]: value });

    // ÏóêÎü¨ ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
    if (errors[field]) {
      setErrors({ ...errors, [field]: '' });
    }
  };

  const validateContent = () => {
    const newErrors: { [key: string]: string } = {};

    // Ï†úÎ™© Í≤ÄÏ¶ù
    if (!content.title.trim()) {
      newErrors.title = 'Ï†úÎ™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî';
    } else if (content.title.length > 50) {
      newErrors.title = 'Ï†úÎ™©ÏùÄ 50Ïûê Ïù¥ÎÇ¥Î°ú ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî';
    }

    // ÏµúÏÜå 1Í∞ú ÎåÄÏÇ¨ Í≤ÄÏ¶ù
    const filledScripts = getFilledScriptCount();
    if (filledScripts === 0) {
      newErrors.body1 = 'ÏµúÏÜå 1Í∞ú Ïù¥ÏÉÅÏùò ÎåÄÏÇ¨Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî';
    }

    // Í∞Å ÎåÄÏÇ¨ Í∏∏Ïù¥ Í≤ÄÏ¶ù
    for (let i = 1; i <= scriptCount; i++) {
      const bodyKey = `body${i}` as keyof ReelsContent;
      const bodyValue = content[bodyKey]?.trim() || '';

      if (bodyValue && bodyValue.length > 200) {
        newErrors[bodyKey] = `ÎåÄÏÇ¨ ${i}ÏùÄ 200Ïûê Ïù¥ÎÇ¥Î°ú ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî`;
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => {
    if (validateContent()) {
      onNext();
    }
  };

  const getCharacterCount = (text: string) => {
    return text ? text.length : 0;
  };

  const getEstimatedDuration = () => {
    let totalChars = 0;
    for (let i = 1; i <= 8; i++) {
      const bodyKey = `body${i}` as keyof ReelsContent;
      totalChars += getCharacterCount(content[bodyKey] || '');
    }
    // ÌïúÍµ≠Ïñ¥ Í∏∞Ï§Ä: Î∂ÑÎãπ 300Ïûê, 1.5Î∞∞ÏÜç Ï†ÅÏö© (Î∂ÑÎãπ 450Ïûê)
    const duration = Math.max(5, Math.ceil((totalChars / 450) * 60));
    return duration;
  };

  // JSON ÌååÏã± Ìï®Ïàò
  const parseJsonContent = (jsonString: string): ReelsContent | null => {
    try {
      const parsed = JSON.parse(jsonString);

      // ÌïÑÏàò ÌïÑÎìú Í≤ÄÏ¶ù
      if (!parsed.title) {
        setJsonError('title ÌïÑÎìúÍ∞Ä ÌïÑÏöîÌï©ÎãàÎã§.');
        return null;
      }

      // ReelsContent ÌòïÌÉúÎ°ú Î≥ÄÌôò
      const newContent: ReelsContent = {
        title: parsed.title.substring(0, 50), // Í∏∏Ïù¥ Ï†úÌïú
        body1: '',
        body2: '',
        body3: '',
        body4: '',
        body5: '',
        body6: '',
        body7: '',
        body8: '',
      };

      // body ÌïÑÎìúÎì§ Ï∂îÍ∞Ä (ÏµúÎåÄ 8Í∞ú)
      let bodyCount = 0;
      for (let i = 1; i <= 8; i++) {
        const bodyKey = `body${i}`;
        if (parsed[bodyKey]) {
          const bodyText = parsed[bodyKey].substring(0, 200); // Í∏∏Ïù¥ Ï†úÌïú
          newContent[bodyKey as keyof ReelsContent] = bodyText;
          bodyCount = i;
        }
      }

      setJsonError('');
      return newContent;
    } catch (error) {
      setJsonError('Ïò¨Î∞îÎ•∏ JSON ÌòïÏãùÏù¥ ÏïÑÎãôÎãàÎã§.');
      return null;
    }
  };

  // JSONÏóêÏÑú ÏΩòÌÖêÏ∏† Î∂àÎü¨Ïò§Í∏∞
  const handleJsonImport = () => {
    if (!jsonInput.trim()) {
      setJsonError('JSON ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    const parsed = parseJsonContent(jsonInput);
    if (parsed) {
      setParsedContent(parsed);

      // Í∏∞Ï°¥ ÎÇ¥Ïö©Ïù¥ ÏûàÎäîÏßÄ ÌôïÏù∏
      const hasExistingContent = content.title.trim() || getFilledScriptCount() > 0;

      if (hasExistingContent) {
        setShowOverwriteDialog(true);
      } else {
        applyParsedContent(parsed);
      }
    }
  };

  // ÌååÏã±Îêú ÏΩòÌÖêÏ∏† Ï†ÅÏö©
  const applyParsedContent = (newContent: ReelsContent) => {
    onChange(newContent);

    // script count ÏûêÎèô Ï°∞Ï†ï
    let maxBodyIndex = 0;
    for (let i = 1; i <= 8; i++) {
      const bodyKey = `body${i}` as keyof ReelsContent;
      if (newContent[bodyKey]?.trim()) {
        maxBodyIndex = i;
      }
    }
    setScriptCount(Math.max(2, maxBodyIndex)); // ÏµúÏÜå 2Í∞ú

    setJsonInput('');
    setShowOverwriteDialog(false);
    setParsedContent(null);
  };

  // ÎçÆÏñ¥Ïì∞Í∏∞ ÌôïÏù∏
  const handleOverwriteConfirm = () => {
    if (parsedContent) {
      applyParsedContent(parsedContent);
    }
  };

  // ÏòàÏ†ú JSON Î∂àÎü¨Ïò§Í∏∞
  const loadExampleJson = () => {
    const exampleJson = {
      "title": "Í≤∞Ìòº 5Í∞úÏõî Ï†Ñ, Ïù¥Í±∏ Î¥êÎ≤ÑÎ†∏Îã§‚Ä¶üíî",
      "body1": "ÌéúÏÖòÏóêÏÑú ÏªµÎùºÎ©¥ Î®πÏúºÎ†§Îã§, Ïª§ÌîºÌè¨Ìä∏ Î¨ºÏù¥ ÌäÄÏñ¥ Î∞úÎì± ÌôîÏÉÅ! üî•",
      "body2": "ÎÑàÎ¨¥ ÏïÑÌååÏÑú Ï£ºÏ†ÄÏïâÏïòÎäîÎç∞‚Ä¶ ÏòàÎπÑÏã†Îûë? Í∑∏ÎÉ• 'Ìóê' ÌïúÎßàÎîî ÌïòÍ≥† ÏÜåÌåå Í∞ÄÏÑú Ïú†ÌäúÎ∏å ÌãÄÏùå‚Ä¶üì±",
      "body3": "Ïã¨ÏßÄÏñ¥ 'Í¥úÏ∞ÆÏïÑ?' Ïù¥ ÌïúÎßàÎîîÎèÑ ÏóÜÏùå. ÎèôÌÉúÎààÏúºÎ°ú Îπ§Ìûà Ï≥êÎã§Î≥∏ Í≤å Îã§ÏûÑüò®",
      "body4": "Ïù¥Ï†ÑÏóêÎèÑ Í≥µÍ∞ê Ï†úÎ°ú, ÎÇ¥ ÏñòÍ∏∞ÌïòÎ©¥ Î¨¥Î∞òÏùë, ÏõÉÍ≤®ÎèÑ Ï†ïÏÉâ‚Ä¶ Ïù¥Î≤àÏóî ÏßÑÏßú ÌÑ∞Ïßê.",
      "body5": "Îî∞ÏßÄÎãàÍπå 'ÎÇ¥Í∞Ä Í¥úÏ∞ÆÎÉêÍ≥† Î¨ºÏúºÎ©¥ ÎÇ´ÎÉê? Ïó¨ÏûêÎì§ÏùÄ ÎÑàÎ¨¥ Í∏∞ÎåÄÍ∞Ä ÌÅ¨Îã§' Ïù¥Îî¥ ÏÜåÎ¶¨ü§¶",
      "body6": "Í≤∞Ìòº ÏïΩÏÜçÌïòÎãàÍπå Ïù¥Ï†ú Î≥¥Ïù∏Îã§. Ïù¥Í∏∞Ï†ÅÏù¥Í≥† Í≥µÍ∞ê ÏóÜÎäî ÎÇ®ÏûêÎùºÎäî Í±∞.",
      "body7": "Î∞±ÎÖÑÌï¥Î°ú? ÏïÑÎãà, ÏòàÏãùÏû• Ï∑®ÏÜåÎ∂ÄÌÑ∞ Í≤ÄÏÉâ Ï§ë„Öã„Öã ÎÇ¥Í∞Ä ÎÑàÎ¨¥Ìïú Í±∏Íπå? ü§î"
    };
    setJsonInput(JSON.stringify(exampleJson, null, 2));
  };

  // URLÏóêÏÑú Î¶¥Ïä§ ÎåÄÎ≥∏ Ï∂îÏ∂ú
  const handleExtractFromUrl = async () => {
    if (!urlInput.trim()) {
      setExtractError('URLÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    // URL ÌòïÏãù Í≤ÄÏ¶ù
    try {
      new URL(urlInput);
    } catch {
      setExtractError('Ïò¨Î∞îÎ•∏ URL ÌòïÏãùÏù¥ ÏïÑÎãôÎãàÎã§.');
      return;
    }

    setIsExtracting(true);
    setExtractError('');

    try {
      const response = await fetch('/extract-reels-from-url', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url: urlInput }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (data.status === 'success') {
        // JSON ÏûÖÎ†• ÏòÅÏó≠Ïóê Í≤∞Í≥º ÏÑ§Ï†ï
        setJsonInput(JSON.stringify(data.reels_content, null, 2));
        setExtractError('');

        // ÏÑ±Í≥µ Î©îÏãúÏßÄ ÌëúÏãú (ÏÑ†ÌÉùÏÇ¨Ìï≠)
        setTimeout(() => {
          setUrlInput(''); // URL ÏûÖÎ†• Ï¥àÍ∏∞Ìôî
        }, 1000);
      } else {
        throw new Error(data.message || 'Î¶¥Ïä§ ÎåÄÎ≥∏ Ï∂îÏ∂úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('URL Ï∂îÏ∂ú ÏóêÎü¨:', error);
      setExtractError(error instanceof Error ? error.message : 'Ïïå Ïàò ÏóÜÎäî Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setIsExtracting(false);
    }
  };

  // URL ÏûÖÎ†• Î≥ÄÍ≤Ω Ï≤òÎ¶¨
  const handleUrlChange = (value: string) => {
    setUrlInput(value);
    if (extractError) {
      setExtractError('');
    }
  };

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        Î¶¥Ïä§ ÎåÄÎ≥∏ ÏûëÏÑ±
      </Typography>
      <Typography variant="body1" color="text.secondary" sx={{ mb: 3 }}>
        Î¶¥Ïä§ ÏòÅÏÉÅÏóê ÏÇ¨Ïö©Ìï† Ï†úÎ™©Í≥º ÎåÄÏÇ¨Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî. URLÏóêÏÑú ÏûêÎèôÏúºÎ°ú ÎåÄÎ≥∏ÏùÑ ÏÉùÏÑ±ÌïòÍ±∞ÎÇò ÏßÅÏ†ë ÏûÖÎ†•Ìï† Ïàò ÏûàÏäµÎãàÎã§.
      </Typography>

      {/* URLÏóêÏÑú Î¶¥Ïä§ ÎåÄÎ≥∏ Ï∂îÏ∂ú ÏòÅÏó≠ */}
      <Paper sx={{ p: 3, mb: 3, bgcolor: 'primary.50', border: '1px solid', borderColor: 'primary.200' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
          <AutoAwesome color="primary" />
          <Typography variant="h6" color="primary">
            AIÎ°ú Î¶¥Ïä§ ÎåÄÎ≥∏ ÏûêÎèô ÏÉùÏÑ±
          </Typography>
        </Box>

        <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
          ÏõπÏÇ¨Ïù¥Ìä∏ URLÏùÑ ÏûÖÎ†•ÌïòÎ©¥ AIÍ∞Ä Ìï¥Îãπ ÌéòÏù¥ÏßÄÏùò ÎÇ¥Ïö©ÏùÑ Î∂ÑÏÑùÌïòÏó¨ Î¶¥Ïä§Ïö© ÎåÄÎ≥∏ÏùÑ ÏûêÎèôÏúºÎ°ú ÏÉùÏÑ±Ìï¥ÎìúÎ¶ΩÎãàÎã§.
        </Typography>

        <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
          <TextField
            label="ÏõπÏÇ¨Ïù¥Ìä∏ URL"
            value={urlInput}
            onChange={(e) => handleUrlChange(e.target.value)}
            placeholder="https://example.com/article"
            fullWidth
            error={!!extractError}
            helperText={extractError}
            disabled={isExtracting}
            InputProps={{
              startAdornment: <Link sx={{ mr: 1, color: 'text.secondary' }} />,
            }}
          />
          <Button
            variant="contained"
            onClick={handleExtractFromUrl}
            disabled={isExtracting || !urlInput.trim()}
            sx={{ minWidth: 140 }}
            startIcon={isExtracting ? <CircularProgress size={16} /> : <AutoAwesome />}
          >
            {isExtracting ? 'Ï∂îÏ∂ú Ï§ë...' : 'Î¶¥Ïä§ JSON Ï∂îÏ∂ú'}
          </Button>
        </Box>

        {isExtracting && (
          <Alert severity="info" sx={{ mb: 2 }}>
            <Typography variant="body2">
              AIÍ∞Ä ÌéòÏù¥ÏßÄ ÎÇ¥Ïö©ÏùÑ Î∂ÑÏÑùÌïòÍ≥† Î¶¥Ïä§Ïö© ÎåÄÎ≥∏ÏùÑ ÏÉùÏÑ±ÌïòÍ≥† ÏûàÏäµÎãàÎã§... Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.
            </Typography>
          </Alert>
        )}
      </Paper>

      <Grid container spacing={3}>
        {/* ÏôºÏ™Ω: ÏûÖÎ†• Ìèº */}
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 3 }}>
            {/* Ï†úÎ™© ÏûÖÎ†• */}
            <TextField
              label="ÏòÅÏÉÅ Ï†úÎ™©"
              value={content.title}
              onChange={(e) => handleChange('title', e.target.value)}
              fullWidth
              required
              error={!!errors.title}
              helperText={errors.title || `${getCharacterCount(content.title)}/50Ïûê`}
              sx={{ mb: 3 }}
              inputProps={{ maxLength: 50 }}
            />

            {/* ÌÖçÏä§Ìä∏ ÏúÑÏπò ÏÑ†ÌÉù */}
            <FormControl sx={{ mb: 3 }}>
              <FormLabel component="legend">ÌÖçÏä§Ìä∏ ÏúÑÏπò ÏÑ†ÌÉù</FormLabel>
              <RadioGroup
                row
                value={textPosition}
                onChange={(e) => onTextPositionChange(e.target.value as TextPosition)}
              >
                <FormControlLabel
                  value="top"
                  control={<Radio />}
                  label="ÏÉÅ (ÌÉÄÏù¥ÌãÄ ÏïÑÎûò)"
                />
                <FormControlLabel
                  value="middle"
                  control={<Radio />}
                  label="Ï§ë (ÌôîÎ©¥ Ï§ëÏïô)"
                />
                <FormControlLabel
                  value="bottom"
                  control={<Radio />}
                  label="Ìïò (ÌôîÎ©¥ ÌïòÎã®)"
                />
              </RadioGroup>
            </FormControl>

            {/* JSON ÏùºÍ¥Ñ ÏûÖÎ†• ÏòÅÏó≠ */}
            <Accordion sx={{ mb: 3 }}>
              <AccordionSummary expandIcon={<ExpandMore />}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Upload color="primary" />
                  <Typography variant="h6">JSONÏúºÎ°ú ÏùºÍ¥Ñ ÏûÖÎ†•</Typography>
                  <Tooltip title="JSON ÌòïÏãù ÎèÑÏõÄÎßê">
                    <IconButton size="small" onClick={() => setShowJsonHelp(true)}>
                      <Help fontSize="small" />
                    </IconButton>
                  </Tooltip>
                </Box>
              </AccordionSummary>
              <AccordionDetails>
                <Box>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    JSON ÌòïÌÉúÎ°ú Ï†úÎ™©Í≥º ÎåÄÏÇ¨Î•º Ìïú Î≤àÏóê ÏûÖÎ†•Ìï† Ïàò ÏûàÏäµÎãàÎã§.
                  </Typography>

                  <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                    <Button
                      variant="outlined"
                      size="small"
                      onClick={loadExampleJson}
                      startIcon={<ContentCopy />}
                    >
                      ÏòàÏ†ú Î∂àÎü¨Ïò§Í∏∞
                    </Button>
                  </Box>

                  <TextField
                    label="JSON Îç∞Ïù¥ÌÑ∞"
                    value={jsonInput}
                    onChange={(e) => setJsonInput(e.target.value)}
                    multiline
                    rows={8}
                    fullWidth
                    placeholder='{\n  "title": "ÏòÅÏÉÅ Ï†úÎ™©",\n  "body1": "Ï≤´ Î≤àÏß∏ ÎåÄÏÇ¨",\n  "body2": "Îëê Î≤àÏß∏ ÎåÄÏÇ¨"\n}'
                    error={!!jsonError}
                    helperText={jsonError}
                    sx={{ mb: 2 }}
                  />

                  <Button
                    variant="contained"
                    onClick={handleJsonImport}
                    startIcon={<Upload />}
                    disabled={!jsonInput.trim()}
                  >
                    JSONÏóêÏÑú Î∂àÎü¨Ïò§Í∏∞
                  </Button>
                </Box>
              </AccordionDetails>
            </Accordion>

            {/* ÎåÄÏÇ¨ ÏûÖÎ†• ÏÑπÏÖò */}
            <Box sx={{ mb: 3 }}>
              <Box display="flex" alignItems="center" justifyContent="space-between" sx={{ mb: 2 }}>
                <Typography variant="h6">ÎåÄÏÇ¨ ÏûÖÎ†•</Typography>
                <Box>
                  <Button
                    size="small"
                    onClick={removeScript}
                    disabled={scriptCount <= 1}
                    startIcon={<Remove />}
                    sx={{ mr: 1 }}
                  >
                    Ï†úÍ±∞
                  </Button>
                  <Button
                    size="small"
                    onClick={addScript}
                    disabled={scriptCount >= 8}
                    startIcon={<Add />}
                    variant="outlined"
                  >
                    Ï∂îÍ∞Ä
                  </Button>
                </Box>
              </Box>

              {/* ÎåÄÏÇ¨ ÏûÖÎ†• ÌïÑÎìúÎì§ */}
              {Array.from({ length: scriptCount }, (_, i) => i + 1).map((num) => {
                const bodyKey = `body${num}` as keyof ReelsContent;
                const value = content[bodyKey] || '';

                return (
                  <TextField
                    key={bodyKey}
                    label={`ÎåÄÏÇ¨ ${num}`}
                    value={value}
                    onChange={(e) => handleChange(bodyKey, e.target.value)}
                    fullWidth
                    multiline
                    rows={3}
                    error={!!errors[bodyKey]}
