import React, { useState, useCallback, useMemo, memo, useEffect, useRef } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  IconButton,
  Chip,
  Alert,
  CircularProgress,
  TextField,
  FormControlLabel,
  Switch,
  Collapse,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import {
  CloudUpload,
  Delete,
  AutoFixHigh,
  Refresh,
  Image as ImageIcon,
  Download,
  Edit as EditIcon,
  ExpandMore,
  ExpandLess,
  Movie as MovieIcon,
  Warning,
  VideoLibrary
} from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';
import { ReelsContent, ImageUploadMode, TextImagePair, CustomPrompt } from '../types';

interface TextImagePairManagerProps {
  content: ReelsContent;
  imageUploadMode: ImageUploadMode;
  images: File[];
  onChange: (images: File[], mode: ImageUploadMode) => void;
  jobId: string | null;  // Job ID Ï∂îÍ∞Ä
}

const TextImagePairManager: React.FC<TextImagePairManagerProps> = ({
  content,
  imageUploadMode,
  images,
  onChange,
  jobId,
}) => {
  const [generationStatus, setGenerationStatus] = useState<{ [key: string]: string }>({});
  const [uploadErrors, setUploadErrors] = useState<{ [key: number]: string }>({});
  const [customPrompts, setCustomPrompts] = useState<{ [key: number]: CustomPrompt }>({});
  const [promptsExpanded, setPromptsExpanded] = useState<{ [key: number]: boolean }>({});

  // ÏµúÍ∑º ÎèôÏòÅÏÉÅ Í¥ÄÎ¶¨ ÏÉÅÌÉú
  const [videoDialogOpen, setVideoDialogOpen] = useState(false);
  const [recentVideos, setRecentVideos] = useState<Array<{
    filename: string;
    path: string;
    thumbnailUrl: string | null;
    size: string;
    modifiedDate: string;
    duration?: string;
    resolution?: string;
    fps?: string;
  }>>([]);
  const [loadingVideos, setLoadingVideos] = useState(false);
  const [selectedVideoForIndex, setSelectedVideoForIndex] = useState<number | null>(null);

  // ÏµúÏã† images ÏÉÅÌÉúÎ•º Ï∂îÏ†ÅÌïòÍ∏∞ ÏúÑÌïú ref
  const imagesRef = useRef<File[]>(images);

  // images propÏù¥ Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ ref ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    imagesRef.current = images;
  }, [images]);

  // Ïª§Ïä§ÌÖÄ ÌîÑÎ°¨ÌîÑÌä∏ Í¥ÄÎ¶¨ Ìï®ÏàòÎì§ (useCallbackÏúºÎ°ú ÏµúÏ†ÅÌôî)
  const updateCustomPrompt = useCallback((imageIndex: number, prompt: string, enabled: boolean) => {
    setCustomPrompts(prev => {
      const currentPrompt = prev[imageIndex];
      // Í∞íÏù¥ Í∞ôÏúºÎ©¥ ÏóÖÎç∞Ïù¥Ìä∏ÌïòÏßÄ ÏïäÏùå (Î∂àÌïÑÏöîÌïú Î¶¨Î†åÎçîÎßÅ Î∞©ÏßÄ)
      if (currentPrompt &&
          currentPrompt.prompt === prompt &&
          currentPrompt.enabled === enabled) {
        return prev;
      }

      return {
        ...prev,
        [imageIndex]: {
          imageIndex,
          prompt,
          enabled
        }
      };
    });
  }, []);

  const togglePromptExpanded = useCallback((imageIndex: number) => {
    setPromptsExpanded(prev => ({
      ...prev,
      [imageIndex]: !prev[imageIndex]
    }));
  }, []);

  // ÌÖçÏä§Ìä∏-Ïù¥ÎØ∏ÏßÄ Ïåç Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ± (useMemoÎ°ú ÏµúÏ†ÅÌôî)
  const textImagePairs = useMemo((): (TextImagePair & { imageIndex: number })[] => {
    const bodyTexts = Object.entries(content)
      .filter(([key, value]) => key.startsWith('body') && value?.trim())
      .map(([key, value], index) => ({ key, value: value.trim(), index }));

    // ÏúÑÏπò Ï†ïÎ≥¥Î•º Í∞ÄÏßÑ Ïù¥ÎØ∏ÏßÄÎì§ÏùÑ ÎßµÏúºÎ°ú ÏÉùÏÑ±
    const imageMap = new Map<number, File>();
    images.forEach(img => {
      const storedIndex = (img as any).__imageIndex;
      if (typeof storedIndex === 'number') {
        imageMap.set(storedIndex, img);
      }
    });

    console.log('üó∫Ô∏è createTextImagePairs - imageMap:', Array.from(imageMap.entries()).map(([idx, file]) => `${idx}:${file.name}`));
    console.log('üìù bodyTexts Í∏∏Ïù¥:', bodyTexts.length, 'imageUploadMode:', imageUploadMode);
    console.log('üîç Î∞õÏùÄ images Î∞∞Ïó¥:', images.map(img => `${img.name}(__imageIndex:${(img as any).__imageIndex})`));

    const pairs: (TextImagePair & { imageIndex: number })[] = [];

    if (imageUploadMode === 'per-script') {
      // ÌÖçÏä§Ìä∏ 1Í∞úÎãπ Ïù¥ÎØ∏ÏßÄ 1Í∞ú
      bodyTexts.forEach(({ key, value, index }) => {
        const foundImage = imageMap.get(index);
        const customPrompt = customPrompts[index];
        console.log(`üìã per-script: textIndex=${index}, imageIndex=${index}, foundImage=${foundImage?.name || 'null'}`);
        pairs.push({
          textIndex: index,
          textKey: key,
          textContent: value,
          image: foundImage || null,
          imageIndex: index, // Ïã§Ï†ú Ïù¥ÎØ∏ÏßÄ Ïù∏Îç±Ïä§ Ï†ÄÏû•
          isGenerating: false,
          customPrompt: customPrompt?.prompt || '',
          useCustomPrompt: customPrompt?.enabled || false,
        });
      });
    } else if (imageUploadMode === 'per-two-scripts') {
      // ÌÖçÏä§Ìä∏ 2Í∞úÎãπ Ïù¥ÎØ∏ÏßÄ 1Í∞ú
      for (let i = 0; i < bodyTexts.length; i += 2) {
        const imageIndex = Math.floor(i / 2);
        const text1 = bodyTexts[i];
        const text2 = bodyTexts[i + 1];

        if (text1) {
          const foundImage = imageMap.get(imageIndex);
          const customPrompt = customPrompts[imageIndex];
          console.log(`üìã per-two-scripts: textIndex=${i}, imageIndex=${imageIndex}, foundImage=${foundImage?.name || 'null'}`);
          pairs.push({
            textIndex: i,
            textKey: `${text1.key}${text2 ? `+${text2.key}` : ''}`,
            textContent: `${text1.value}${text2 ? ` / ${text2.value}` : ''}`,
            image: foundImage || null,
            imageIndex: imageIndex, // Ïã§Ï†ú Ïù¥ÎØ∏ÏßÄ Ïù∏Îç±Ïä§ Ï†ÄÏû•
            isGenerating: false,
            customPrompt: customPrompt?.prompt || '',
            useCustomPrompt: customPrompt?.enabled || false,
          });
        }
      }
    } else if (imageUploadMode === 'single-for-all') {
      // Î™®Îì† ÌÖçÏä§Ìä∏Ïóê Ïù¥ÎØ∏ÏßÄ 1Í∞ú
      const allTexts = bodyTexts.map(({ key, value }) => key.replace('body', 'ÎåÄÏÇ¨')).join(' + ');
      const allContent = bodyTexts.map(({ value }) => value).join(' / ');
      const foundImage = imageMap.get(0); // Ï≤´ Î≤àÏß∏ (Í∑∏Î¶¨Í≥† Ïú†ÏùºÌïú) Ïù¥ÎØ∏ÏßÄ
      const customPrompt = customPrompts[0];

      console.log(`üìã single-for-all: Î™®Îì† ÎåÄÏÇ¨, imageIndex=0, foundImage=${foundImage?.name || 'null'}`);
      pairs.push({
        textIndex: 0,
        textKey: allTexts,
        textContent: allContent,
        image: foundImage || null,
        imageIndex: 0, // Ìï≠ÏÉÅ Ï≤´ Î≤àÏß∏ Ïù¥ÎØ∏ÏßÄ Ïù∏Îç±Ïä§ ÏÇ¨Ïö©
        isGenerating: false,
        customPrompt: customPrompt?.prompt || '',
        useCustomPrompt: customPrompt?.enabled || false,
      });
    }

    console.log('üéØ ÏµúÏ¢Ö pairs Í∞úÏàò:', pairs.length);
    return pairs;
  }, [content, images, imageUploadMode, customPrompts]);

  // ÌòÑÏû¨ images Î∞∞Ïó¥ ÏÉÅÌÉú Î°úÍπÖ
  console.log('üì¶ ÌòÑÏû¨ images Î∞∞Ïó¥ Í∏∏Ïù¥:', images.length);
  console.log('üì¶ ÌòÑÏû¨ images Î∞∞Ïó¥:', images.length > 0 ? images.map(img => `${img.name}(index:${(img as any).__imageIndex})`) : 'Îπà Î∞∞Ïó¥');
  console.log('üéÆ generationStatus:', generationStatus);

  // Í∞úÎ≥Ñ Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú Ìï∏Îì§Îü¨
  const handleIndividualImageUpload = useCallback((imageIndex: number, acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    const file = acceptedFiles[0];

    // Í∏∞Ï°¥ ÏóêÎü¨ ÏÉÅÌÉú Ï†úÍ±∞
    setUploadErrors(prev => {
      const newErrors = { ...prev };
      delete newErrors[imageIndex];
      return newErrors;
    });

    // ÌååÏùº Ïú†Ìö®ÏÑ± Í≤ÄÏ¶ù (Î™®ÎìúÏóê Îî∞Îùº Îã§Î•∏ Ï†úÌïú)
    const maxSize = imageUploadMode === 'single-for-all' ? 40 * 1024 * 1024 : 10 * 1024 * 1024;
    const maxSizeText = imageUploadMode === 'single-for-all' ? '40MB' : '10MB';

    if (file.size > maxSize) {
      setUploadErrors(prev => ({ ...prev, [imageIndex]: `ÌååÏùº ÌÅ¨Í∏∞Í∞Ä ${maxSizeText}Î•º Ï¥àÍ≥ºÌï©ÎãàÎã§` }));
      return;
    }

    const isImage = file.type.startsWith('image/');
    const isVideo = file.type.startsWith('video/');

    if (!isImage && !isVideo) {
      setUploadErrors(prev => ({ ...prev, [imageIndex]: 'Ïù¥ÎØ∏ÏßÄ ÎòêÎäî ÎπÑÎîîÏò§ ÌååÏùºÎßå ÏóÖÎ°úÎìú Í∞ÄÎä•Ìï©ÎãàÎã§' }));
      return;
    }

    console.log('üîß handleIndividualImageUpload - imageIndex:', imageIndex, 'fileName:', file.name);

    // ÌååÏùºÏóê ÏúÑÏπò Ï†ïÎ≥¥ Ï∂îÍ∞Ä
    (file as any).__imageIndex = imageIndex;
    console.log('üè∑Ô∏è ÌååÏùºÏóê __imageIndex ÏÑ§Ï†ï:', imageIndex);

    // ÏµúÏã† images ÏÉÅÌÉúÎ•º Í∞ÄÏ†∏ÏôÄÏÑú ÏóÖÎç∞Ïù¥Ìä∏
    const currentImages = imagesRef.current;
    console.log('üîÑ ÏóÖÎç∞Ïù¥Ìä∏ Ï†Ñ currentImages (ref):', currentImages.map(img => `${img.name}(index:${(img as any).__imageIndex})`));

    // ÌòÑÏû¨ imagesÏóêÏÑú Ìï¥Îãπ imageIndexÎ•º Í∞ÄÏßÑ ÌååÏùº Ï†úÍ±∞ÌïòÍ≥† ÏÉà ÌååÏùº Ï∂îÍ∞Ä
    const newImages = currentImages.filter(img => (img as any).__imageIndex !== imageIndex);
    newImages.push(file);

    // imageIndex ÏàúÏúºÎ°ú Ï†ïÎ†¨
    newImages.sort((a, b) => {
      const indexA = (a as any).__imageIndex ?? 0;
      const indexB = (b as any).__imageIndex ?? 0;
      return indexA - indexB;
    });

    console.log('üìä ÏóÖÎç∞Ïù¥Ìä∏Îêú images Î∞∞Ïó¥:', newImages.map(img => `${img.name}(index:${(img as any).__imageIndex})`));

    onChange(newImages, imageUploadMode);
