  // Í∞úÎ≥Ñ Ïù¥ÎØ∏ÏßÄ ÏûêÎèô ÏÉùÏÑ±
  const handleIndividualGenerate = async (imageIndex: number, textContent: string, customPrompt?: string, useCustomPrompt?: boolean) => {
    console.log('ü§ñ handleIndividualGenerate ÏãúÏûë - imageIndex:', imageIndex, 'textContent:', textContent, 'customPrompt:', customPrompt, 'useCustomPrompt:', useCustomPrompt);
    setGenerationStatus(prev => ({ ...prev, [imageIndex]: 'generating' }));

    try {
      // ÏöîÏ≤≠ Î∞îÎîî Íµ¨ÏÑ±
      let requestBody: any = {};

      // job_id Ï∂îÍ∞Ä (job-based folder isolationÏùÑ ÏúÑÌï¥)
      if (jobId) {
        requestBody.job_id = jobId;
        console.log('üÜî job_id Ï∂îÍ∞Ä:', jobId);
      }

      if (useCustomPrompt && customPrompt?.trim()) {
        // Ïª§Ïä§ÌÖÄ ÌîÑÎ°¨ÌîÑÌä∏ ÏÇ¨Ïö©
        requestBody.custom_prompt = customPrompt.trim();
        console.log('üìù Ïª§Ïä§ÌÖÄ ÌîÑÎ°¨ÌîÑÌä∏ ÏÇ¨Ïö©:', customPrompt.trim());
      } else {
        // Í∏∞Ï°¥ ÌÖçÏä§Ìä∏ ÏÇ¨Ïö©
        const texts = textContent.split(' / ');
        requestBody.text = texts[0]; // Ï≤´ Î≤àÏß∏ ÌÖçÏä§Ìä∏ ÏÇ¨Ïö©
        if (texts.length > 1) {
          requestBody.additional_context = texts[1];
        }
        console.log('üìù Í∏∞Î≥∏ ÌÖçÏä§Ìä∏ ÏÇ¨Ïö©:', texts[0]);
      }

      const response = await fetch('/generate-single-image', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
      }

      const data = await response.json();
      let imageUrl = data.image_url;

      // job_idÍ∞Ä ÏûàÎäî Í≤ΩÏö∞ job-specific URLÎ°ú Î≥ÄÍ≤Ω
      if (jobId && imageUrl.includes('/get-image/')) {
        const fileName = imageUrl.split('/get-image/')[1];
        imageUrl = `/job-uploads/${jobId}/${fileName}`;
        console.log('üîÑ job-specific URLÎ°ú Î≥ÄÍ≤Ω:', imageUrl);
      }

      // ÏÉùÏÑ±Îêú Ïù¥ÎØ∏ÏßÄÎ•º Îã§Ïö¥Î°úÎìúÌïòÍ≥† File Í∞ùÏ≤¥Î°ú Î≥ÄÌôò
      const imageResponse = await fetch(imageUrl);
      if (!imageResponse.ok) {
        throw new Error(`ÏÉùÏÑ±Îêú Ïù¥ÎØ∏ÏßÄ Îã§Ïö¥Î°úÎìúÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§: ${imageResponse.status}`);
      }

      const blob = await imageResponse.blob();
      const fileName = `generated_image_pair_${imageIndex}.png`;
      const file = new File([blob], fileName, { type: 'image/png' });

      console.log('üì∏ Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ± ÏôÑÎ£å - fileName:', fileName, 'Ìò∏Ï∂úÌï† imageIndex:', imageIndex);

      // Ïù¥ÎØ∏ÏßÄ Î∞∞Ïó¥ ÏóÖÎç∞Ïù¥Ìä∏
      handleIndividualImageUpload(imageIndex, [file]);

      setGenerationStatus(prev => ({ ...prev, [imageIndex]: 'success' }));
      setTimeout(() => {
        setGenerationStatus(prev => {
          const newStatus = { ...prev };
          delete newStatus[imageIndex];
          return newStatus;
        });
      }, 3000);

    } catch (error) {
      console.error('Í∞úÎ≥Ñ Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ± Ïò§Î•ò:', error);
      setGenerationStatus(prev => ({ ...prev, [imageIndex]: 'error' }));
      setTimeout(() => {
        setGenerationStatus(prev => {
          const newStatus = { ...prev };
          delete newStatus[imageIndex];
          return newStatus;
        });
      }, 5000);
    }
  };

  // ÏµúÍ∑º ÎèôÏòÅÏÉÅ Î∂àÎü¨Ïò§Í∏∞
