        return; // Îπà Í∞íÏù∏ Í≤ΩÏö∞ ÏóÖÎç∞Ïù¥Ìä∏ ÌïòÏßÄ ÏïäÏùå
      }

      const timeoutId = setTimeout(() => {
        const currentExternalValue = customPrompts[imageIndex]?.prompt || '';
