import React, { useState, useCallback, useMemo, memo } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  IconButton,
  Chip,
  Alert,
  CircularProgress,
  TextField,
  FormControlLabel,
  Switch,
  Collapse
} from '@mui/material';
import {
  CloudUpload,
  Delete,
  AutoFixHigh,
  Refresh,
  Image as ImageIcon,
  Download,
  Edit as EditIcon,
  ExpandMore,
  ExpandLess,
  Movie as MovieIcon,
  Warning
} from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';
import { ReelsContent, ImageUploadMode, TextImagePair, CustomPrompt } from '../types';

interface TextImagePairManagerProps {
  content: ReelsContent;
  imageUploadMode: ImageUploadMode;
  images: File[];
  onChange: (images: File[], mode: ImageUploadMode) => void;
  jobId: string | null;  // Job ID 추가
}

const TextImagePairManager: React.FC<TextImagePairManagerProps> = ({
  content,
  imageUploadMode,
  images,
  onChange,
  jobId
}) => {
  // 상태 관리
  const [customPrompts, setCustomPrompts] = useState<{ [key: number]: CustomPrompt }>({});
  const [promptsExpanded, setPromptsExpanded] = useState<{ [key: number]: boolean }>({});
  const [imageGenerating, setImageGenerating] = useState<{ [key: number]: boolean }>({});
  const [generationErrors, setGenerationErrors] = useState<{ [key: number]: string }>({});

  // 커스텀 프롬프트 관리 함수들
  const updateCustomPrompt = useCallback((imageIndex: number, prompt: string, enabled: boolean) => {
    setCustomPrompts(prev => ({
      ...prev,
      [imageIndex]: { imageIndex, prompt, enabled }
    }));
  }, []);

  const togglePromptExpanded = useCallback((imageIndex: number) => {
    setPromptsExpanded(prev => ({
      ...prev,
      [imageIndex]: !prev[imageIndex]
    }));
  }, []);

  // 개별 이미지 업로드 처리
  const handleIndividualImageUpload = useCallback((imageIndex: number, files: File[]) => {
    if (files.length === 0) return;

    const file = files[0];
    const fileWithIndex = Object.assign(file, { __imageIndex: imageIndex });

    const currentImages = images.slice();
    const existingIndex = currentImages.findIndex(img => (img as any).__imageIndex === imageIndex);

    if (existingIndex !== -1) {
      currentImages[existingIndex] = fileWithIndex;
    } else {
      currentImages.push(fileWithIndex);
    }

    onChange(currentImages, imageUploadMode);
  }, [images, onChange, imageUploadMode]);

  // 개별 이미지 생성 처리
  const handleIndividualGenerate = useCallback(async (imageIndex: number, textContent: string, customPrompt?: string, useCustomPrompt: boolean = false) => {
    try {
      setImageGenerating(prev => ({ ...prev, [imageIndex]: true }));
      setGenerationErrors(prev => ({ ...prev, [imageIndex]: '' }));

      let requestBody: any = {};

      if (useCustomPrompt && customPrompt?.trim()) {
        requestBody.custom_prompt = customPrompt.trim();
      } else {
        const texts = textContent.split(' / ');
        requestBody.text = texts[0];
        if (texts.length > 1) {
          requestBody.additional_context = texts[1];
        }
      }

      if (jobId) {
        requestBody.job_id = jobId;
      }

      const response = await fetch('/generate-single-image', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (data.status === 'success') {
        const imageUrl = data.image_url;
        let fullUrl = imageUrl;
        if (imageUrl.startsWith('/')) {
          fullUrl = `${window.location.origin}${imageUrl}`;
        }

        const imageResponse = await fetch(fullUrl);
        if (!imageResponse.ok) {
          throw new Error('이미지 다운로드 실패');
        }

        const blob = await imageResponse.blob();
        const fileName = `generated_image_${imageIndex}.png`;
        const image = new File([blob], fileName, { type: 'image/png' });

        handleIndividualImageUpload(imageIndex, [image]);
      } else {
        throw new Error(data.message || '이미지 생성 실패');
      }
    } catch (error) {
      setGenerationErrors(prev => ({
        ...prev,
        [imageIndex]: error instanceof Error ? error.message : '알 수 없는 오류가 발생했습니다'
      }));
    } finally {
      setImageGenerating(prev => ({ ...prev, [imageIndex]: false }));
    }
  }, [handleIndividualImageUpload, jobId]);

  // 이미지 삭제 처리
  const handleIndividualImageDelete = useCallback((imageIndex: number) => {
    const newImages = images.filter(img => (img as any).__imageIndex !== imageIndex);
    onChange(newImages, imageUploadMode);
  }, [images, onChange, imageUploadMode]);

  // 텍스트-이미지 쌍 데이터 생성
  const textImagePairs = useMemo((): (TextImagePair & { imageIndex: number })[] => {
    const bodyTexts = Object.entries(content)
      .filter(([key, value]) => key.startsWith('body') && value?.trim())
      .map(([key, value], index) => ({ key, value: value.trim(), index }));

    const imageMap = new Map<number, File>();
    images.forEach(img => {
      const storedIndex = (img as any).__imageIndex;
      if (typeof storedIndex === 'number') {
        imageMap.set(storedIndex, img);
      }
    });

    const pairs: (TextImagePair & { imageIndex: number })[] = [];

    if (imageUploadMode === 'per-script') {
      bodyTexts.forEach(({ key, value }, index) => {
        const imageIndex = index;
        const foundImage = imageMap.get(imageIndex);
        pairs.push({
          textContent: value,
          imageFile: foundImage || null,
          imageIndex: imageIndex
        });
      });
    } else if (imageUploadMode === 'per-two-scripts') {
      for (let i = 0; i < bodyTexts.length; i += 2) {
        const imageIndex = Math.floor(i / 2);
        const textContents = [bodyTexts[i]?.value];
        if (bodyTexts[i + 1]) {
          textContents.push(bodyTexts[i + 1].value);
        }
        const foundImage = imageMap.get(imageIndex);
        pairs.push({
          textContent: textContents.join(' / '),
          imageFile: foundImage || null,
          imageIndex: imageIndex
        });
      }
    } else {
      const imageIndex = 0;
      const foundImage = imageMap.get(imageIndex);
      const allTexts = bodyTexts.map(bt => bt.value).join(' / ');
      pairs.push({
        textContent: allTexts,
        imageFile: foundImage || null,
        imageIndex: imageIndex
      });
    }

    return pairs;
  }, [content, images, imageUploadMode]);

  // 드래그앤드롭 설정
  const getDropzoneProps = useCallback((imageIndex: number) => {
    return useDropzone({
      accept: {
        'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.webp', '.bmp']
      },
      onDrop: (acceptedFiles) => handleIndividualImageUpload(imageIndex, acceptedFiles),
      multiple: false
    });
  }, [handleIndividualImageUpload]);

  return (
    <Box>
      <Typography variant="h6" gutterBottom sx={{ mb: 3 }}>
        텍스트-이미지 매칭 관리
      </Typography>

      <Grid container spacing={2}>
        {textImagePairs.map((pair, index) => {
          const { getRootProps, getInputProps, isDragActive } = getDropzoneProps(pair.imageIndex);
          const customPrompt = customPrompts[pair.imageIndex];
          const isExpanded = promptsExpanded[pair.imageIndex];
          const isGenerating = imageGenerating[pair.imageIndex];
          const error = generationErrors[pair.imageIndex];

          return (
            <Grid item xs={12} md={6} key={pair.imageIndex}>
              <Card variant="outlined">
                <CardContent>
                  <Typography variant="subtitle2" gutterBottom>
                    {imageUploadMode === 'per-script'
                      ? `대사 ${index + 1}`
                      : imageUploadMode === 'per-two-scripts'
                        ? `대사 ${index * 2 + 1}-${Math.min(index * 2 + 2, Object.keys(content).filter(k => k.startsWith('body')).length)}`
                        : '전체 대사'
                    }
                  </Typography>

                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2, fontSize: '0.85rem' }}>
                    {pair.textContent.length > 100
                      ? `${pair.textContent.substring(0, 100)}...`
                      : pair.textContent}
                  </Typography>

                  {/* 이미지 업로드/표시 영역 */}
                  <Box
                    {...getRootProps()}
                    sx={{
                      border: '2px dashed',
                      borderColor: isDragActive ? 'primary.main' : 'grey.300',
                      borderRadius: 1,
                      p: 2,
                      textAlign: 'center',
                      cursor: 'pointer',
                      bgcolor: isDragActive ? 'action.hover' : 'background.paper',
                      mb: 2,
                      minHeight: 120,
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      alignItems: 'center'
                    }}
                  >
                    <input {...getInputProps()} />

                    {pair.imageFile ? (
                      <Box sx={{ position: 'relative', width: '100%' }}>
                        {pair.imageFile.type.startsWith('image/') ? (
                          <img
                            src={URL.createObjectURL(pair.imageFile)}
                            alt="업로드된 이미지"
                            style={{
                              width: '100%',
                              maxHeight: '150px',
                              objectFit: 'cover',
                              borderRadius: '4px'
                            }}
                          />
                        ) : (
                          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: 100 }}>
                            <MovieIcon sx={{ fontSize: 48, color: 'action.active' }} />
                            <Typography variant="body2" sx={{ ml: 1 }}>
                              {pair.imageFile.name}
                            </Typography>
                          </Box>
                        )}
                        <IconButton
                          size="small"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleIndividualImageDelete(pair.imageIndex);
                          }}
                          sx={{
                            position: 'absolute',
                            top: 4,
                            right: 4,
                            bgcolor: 'background.paper',
                            '&:hover': { bgcolor: 'error.light' }
                          }}
                        >
                          <Delete fontSize="small" />
                        </IconButton>
                      </Box>
                    ) : (
                      <>
                        <CloudUpload sx={{ fontSize: 48, color: 'action.active', mb: 1 }} />
                        <Typography variant="body2" color="text.secondary">
                          {isDragActive ? '파일을 놓아주세요' : '이미지를 드래그하거나 클릭하여 업로드'}
                        </Typography>
                      </>
                    )}
                  </Box>

                  {/* 커스텀 프롬프트 섹션 */}
                  <Box sx={{ mb: 2 }}>
                    <Button
                      size="small"
                      onClick={() => togglePromptExpanded(pair.imageIndex)}
                      startIcon={isExpanded ? <ExpandLess /> : <ExpandMore />}
                      sx={{ mb: 1 }}
                    >
                      커스텀 프롬프트 {isExpanded ? '닫기' : '열기'}
                    </Button>

                    <Collapse in={isExpanded}>
                      <Box sx={{ p: 2, bgcolor: 'grey.50', borderRadius: 1 }}>
                        <FormControlLabel
                          control={
                            <Switch
                              checked={customPrompt?.enabled || false}
                              onChange={(e) => updateCustomPrompt(
                                pair.imageIndex,
                                customPrompt?.prompt || '',
                                e.target.checked
                              )}
                            />
                          }
                          label="커스텀 프롬프트 사용"
                          sx={{ mb: 1 }}
                        />

                        <TextField
                          fullWidth
                          multiline
                          rows={3}
                          placeholder="이미지 생성을 위한 커스텀 프롬프트를 입력하세요..."
                          value={customPrompt?.prompt || ''}
                          onChange={(e) => updateCustomPrompt(
                            pair.imageIndex,
                            e.target.value,
                            customPrompt?.enabled || false
                          )}
                          disabled={!customPrompt?.enabled}
                          sx={{ mt: 1 }}
                        />
                      </Box>
                    </Collapse>
                  </Box>

                  {/* 에러 메시지 */}
                  {error && (
                    <Alert severity="error" sx={{ mb: 2 }}>
                      {error}
                    </Alert>
                  )}

                  {/* 액션 버튼들 */}
                  <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                    <Button
                      size="small"
                      variant="outlined"
                      startIcon={isGenerating ? <CircularProgress size={16} /> : <AutoFixHigh />}
                      onClick={() => handleIndividualGenerate(
                        pair.imageIndex,
                        pair.textContent,
                        customPrompt?.prompt,
                        customPrompt?.enabled
                      )}
                      disabled={isGenerating}
                      sx={{ flex: 1, minWidth: '120px' }}
                    >
                      {isGenerating ? '생성 중...' : 'AI 자동생성'}
                    </Button>

                    {pair.imageFile && (
                      <IconButton
                        size="small"
                        onClick={() => {
                          const url = URL.createObjectURL(pair.imageFile!);
                          const a = document.createElement('a');
                          a.href = url;
                          a.download = pair.imageFile!.name;
                          a.click();
                          URL.revokeObjectURL(url);
                        }}
                        title="이미지 다운로드"
                      >
                        <Download />
                      </IconButton>
                    )}
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          );
        })}
      </Grid>
    </Box>
  );
};

export default memo(TextImagePairManager);
