                const videoExtensions = ['.mp4', '.mov', '.avi', '.webm', '.mkv'];
                const isVideo = pair.image.type.startsWith('video/') || videoExtensions.some(ext => fileName.endsWith(ext));

                // HEIC 파일 확인
