import React, { useState, useRef } from 'react';
import {
  Box,
  Container,
  Stepper,
  Step,
  StepLabel,
  Typography,
  AppBar,
  Toolbar,
  Button,
  Avatar,
  IconButton,
  Menu,
  MenuItem,
} from '@mui/material';
import { AccountCircle, ExitToApp } from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import ContentStep from '../components/ContentStep';
import ImageStep, { ImageStepRef } from '../components/ImageStep';
import MusicStep from '../components/MusicStep';
import GenerateStep from '../components/GenerateStep';
import { ProjectData, ReelsContent, MusicMood, ImageUploadMode, MusicFile, TextPosition, TextStyle, TitleAreaMode, CrossDissolve } from '../types';
import * as apiService from '../services/api';

// UUID 생성 유틸리티 함수
const generateJobId = (): string => {
  return 'job_' + Math.random().toString(36).substr(2, 9) + '_' + Date.now().toString(36);
};

const steps = [
  '릴스 대본 작성',
  '이미지 업로드',
  '음악 선택',
  '영상 생성',
];

const MainApp: React.FC = () => {
  const { user, logout } = useAuth();
  const [activeStep, setActiveStep] = useState(0);
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);

  // 프로젝트 데이터 상태
  const [projectData, setProjectData] = useState<ProjectData>({
    jobId: generateJobId(), // 초기 Job ID 생성
    content: {
      title: '',
      body1: '',
      body2: '',
      body3: '',
      body4: '',
      body5: '',
      body6: '',
      body7: '',
      body8: '',
    },
    images: [],
    imageUploadMode: 'per-two-scripts',
    textPosition: 'bottom',
    textStyle: 'outline',
    titleAreaMode: 'keep',
    selectedMusic: null,
    musicMood: 'bright',
    fontSettings: {
      titleFont: 'BMYEONSUNG_otf.otf',
      bodyFont: 'BMYEONSUNG_otf.otf',
      titleFontSize: 42,
      bodyFontSize: 36,
    },
    voiceNarration: 'enabled',
    crossDissolve: 'enabled',
  });

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
    setProjectData({
      jobId: generateJobId(), // 새로운 Job ID 생성
      content: {
        title: '',
        body1: '',
        body2: '',
        body3: '',
        body4: '',
        body5: '',
        body6: '',
        body7: '',
        body8: '',
      },
      images: [],
      imageUploadMode: 'per-two-scripts',
      textPosition: 'bottom',
      textStyle: 'outline',
      titleAreaMode: 'keep',
      selectedMusic: null,
      musicMood: 'bright',
      fontSettings: {
        titleFont: 'BMYEONSUNG_otf.otf',
        bodyFont: 'BMYEONSUNG_otf.otf',
        titleFontSize: 42,
        bodyFontSize: 36,
      },
      voiceNarration: 'enabled',
      crossDissolve: 'enabled',
    });
  };

  const handleContentChange = (content: ReelsContent) => {
    setProjectData(prev => ({ ...prev, content }));
  };

  const handleImagesChange = (images: File[], mode: ImageUploadMode) => {
    setProjectData(prev => ({
      ...prev,
      images,
      imageUploadMode: mode
    }));
  };

  // ✅ editedTexts는 ImageStep의 ref를 통해 가져오므로 불필요

  const handleMusicChange = (selectedMusic: MusicFile | null, musicMood: MusicMood) => {
    setProjectData(prev => ({
      ...prev,
      selectedMusic,
      musicMood
    }));
  };

  // ContentStep에서 "다음" 버튼을 누를 때 job 폴더 생성
  const handleContentStepNext = async () => {
    try {
      console.log('🚀 Job 폴더 생성 중:', projectData.jobId);

      // Backend에 job 폴더 생성 요청
      await apiService.createJobFolder(projectData.jobId);

      console.log('✅ Job 폴더 생성 완료:', projectData.jobId);

      // 다음 단계로 이동
      handleNext();
    } catch (error) {
      console.error('❌ Job 폴더 생성 실패:', error);

      // Job ID를 새로 생성하고 재시도 또는 사용자에게 알림
      alert('작업 폴더 생성에 실패했습니다. 다시 시도해주세요.');
    }
  };


  const handleMenuClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    handleMenuClose();
    logout();
  };

  const getStepContent = (step: number) => {
    switch (step) {
      case 0:
        return (
          <ContentStep
            content={projectData.content}
            onChange={handleContentChange}
            onNext={handleContentStepNext} // Job 폴더 생성을 포함한 핸들러 사용
          />
        );
      case 1:
        return (
          <ImageStep
            images={projectData.images}
            imageUploadMode={projectData.imageUploadMode}
            content={projectData.content}
            jobId={projectData.jobId} // Job ID 전달
            onChange={handleImagesChange}
            onEditedTextsChange={handleEditedTextsChange} // 수정된 텍스트 핸들러 전달
            onNext={handleNext}
            onBack={handleBack}
          />
        );
      case 2:
        return (
          <MusicStep
            selectedMusic={projectData.selectedMusic}
            musicMood={projectData.musicMood}
            onChange={handleMusicChange}
            onNext={handleNext}
            onBack={handleBack}
          />
        );
      case 3:
        return (
          <GenerateStep
            projectData={projectData}
            onBack={handleBack}
            onReset={handleReset}
          />
        );
      default:
        return null;
    }
  };

  return (
    <Box>
      {/* 상단 헤더 */}
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            릴스 영상 생성기
          </Typography>

          {user && (
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Typography variant="body2" sx={{ mr: 1 }}>
                {user.name}
              </Typography>
              <IconButton
                size="large"
                onClick={handleMenuClick}
                color="inherit"
              >
                {user.picture ? (
                  <Avatar src={user.picture} sx={{ width: 32, height: 32 }} />
                ) : (
                  <AccountCircle />
                )}
              </IconButton>
              <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={handleMenuClose}
              >
                <MenuItem onClick={handleLogout}>
                  <ExitToApp sx={{ mr: 1 }} />
                  로그아웃
                </MenuItem>
              </Menu>
            </Box>
          )}
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        {/* 단계 표시기 */}
        <Box sx={{ mb: 4 }}>
          <Stepper activeStep={activeStep}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>
        </Box>

        {/* 메인 콘텐츠 */}
        <Box>
          {getStepContent(activeStep)}
        </Box>
      </Container>
    </Box>
  );
};

export default MainApp;
