import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  Grid,
  Alert,
  LinearProgress,
  Card,
  CardContent,
  Divider,
  Chip,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControlLabel,
  Switch,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  RadioGroup,
  Radio,
  FormLabel,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Skeleton,
} from '@mui/material';
import { ImageStepRef } from './ImageStep';
import {
  PlayArrow,
  Download,
  Refresh,
  CheckCircle,
  Error as ErrorIcon,
  VideoCall,
  MusicNote,
  Image as ImageIcon,
  TextFields,
  Email,
  Schedule,
  Send,
  ExpandMore,
  Visibility,
  FontDownload,
  Style,
  FormatColorText,
} from '@mui/icons-material';
import { ProjectData, GenerationStatus, AsyncVideoResponse, FontFile, TextPosition, TextStyle, VoiceNarration, TitleAreaMode, CrossDissolve } from '../types';
import apiService from '../services/api';

interface GenerateStepProps {
  projectData: ProjectData;
  imageStepRef: React.RefObject<ImageStepRef>;
  onBack: () => void;
  onReset: () => void;
}

const GenerateStep: React.FC<GenerateStepProps> = ({
  projectData,
  imageStepRef,
  onBack,
  onReset,
}) => {
  // 사용자 정보 가져오기 (임시로 localStorage에서 직접 가져오기)
  const getUserFromStorage = () => {
    try {
      const savedUser = localStorage.getItem('user');
      return savedUser ? JSON.parse(savedUser) : null;
    } catch {
      return null;
    }
  };

  const user = getUserFromStorage();

  // 기존 상태 변수들
  const [generationStatus, setGenerationStatus] = useState<GenerationStatus>('idle');
  const [progress, setProgress] = useState(0);
  const [statusMessage, setStatusMessage] = useState('');
  const [videoPath, setVideoPath] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);

  // 배치 작업 관련 상태 변수들
  const [userEmail, setUserEmail] = useState<string>(user?.email || '');
  const [enableAsyncMode, setEnableAsyncMode] = useState<boolean>(true);
  const [jobId, setJobId] = useState<string | null>(null);
  const [asyncResponse, setAsyncResponse] = useState<AsyncVideoResponse | null>(null);
  const [estimatedTime, setEstimatedTime] = useState<string>('약 3-10분');

  // 폰트 및 스타일 관련 상태 변수들
  const [availableFonts, setAvailableFonts] = useState<FontFile[]>([]);
  const [loadingFonts, setLoadingFonts] = useState(true);
  const [titleFont, setTitleFont] = useState<string>('BMYEONSUNG_otf.otf');
  const [bodyFont, setBodyFont] = useState<string>('BMYEONSUNG_otf.otf');
  const [titleFontSize, setTitleFontSize] = useState<number>(42);
  const [bodyFontSize, setBodyFontSize] = useState<number>(36);
  const [textPosition, setTextPosition] = useState<TextPosition>('bottom');
  const [textStyle, setTextStyle] = useState<TextStyle>('outline');
  const [titleAreaMode, setTitleAreaMode] = useState<TitleAreaMode>('keep');
  const [voiceNarration, setVoiceNarration] = useState<VoiceNarration>('enabled');
  const [crossDissolve, setCrossDissolve] = useState<CrossDissolve>('enabled');
  const [subtitleDuration, setSubtitleDuration] = useState<number>(0);

  // 미리보기 관련 상태
  const [previewImage, setPreviewImage] = useState<string | null>(null);
  const [loadingPreview, setLoadingPreview] = useState(false);

  // ✅ 수정된 텍스트 상태 (화면 표시용)
  const [displayContent, setDisplayContent] = useState(projectData.content);

  // projectData 값으로 초기화 + 수정된 텍스트 수집
  useEffect(() => {
    setTitleFont(projectData.fontSettings.titleFont);
    setBodyFont(projectData.fontSettings.bodyFont);
    setTitleFontSize(projectData.fontSettings.titleFontSize);
    setBodyFontSize(projectData.fontSettings.bodyFontSize);
    setTextPosition(projectData.textPosition);
    setTextStyle(projectData.textStyle);
    setTitleAreaMode(projectData.titleAreaMode);
    setVoiceNarration(projectData.voiceNarration);
    setCrossDissolve(projectData.crossDissolve);

    // ✅ 수정된 텍스트 수집 및 병합
    const { editedTexts } = imageStepRef.current?.getEditedData() || { editedTexts: {} };

    // 원본 content 복사
    const mergedContent = { ...projectData.content };

    // editedTexts가 있으면 병합
    Object.keys(editedTexts).forEach(imageIndexStr => {
      const imageIndex = parseInt(imageIndexStr);
      const texts = editedTexts[imageIndex];

      if (texts && texts.length > 0) {
        // imageIndex에 해당하는 body 키 찾기
        // per-two-scripts: imageIndex 0 -> body1, body2
        // per-script: imageIndex 0 -> body1
        const bodyKeys = Object.keys(projectData.content).filter(k => k.startsWith('body'));

        if (projectData.imageUploadMode === 'per-two-scripts') {
          const startIdx = imageIndex * 2 + 1; // body1부터 시작
          texts.forEach((text, idx) => {
            const bodyKey = `body${startIdx + idx}` as keyof typeof mergedContent;
            if (text && bodyKey in mergedContent) {
              mergedContent[bodyKey] = text;
            }
          });
        } else if (projectData.imageUploadMode === 'per-script') {
          const bodyKey = `body${imageIndex + 1}` as keyof typeof mergedContent;
          if (texts[0] && bodyKey in mergedContent) {
            mergedContent[bodyKey] = texts[0];
          }
        }
      }
    });

    setDisplayContent(mergedContent);
  }, [projectData, imageStepRef]);

  // 예상 생성 시간 계산
  const calculateEstimatedTime = () => {
    const scriptCount = Object.values(projectData.content)
      .slice(1) // title 제외
      .filter(script => script?.trim()).length;

    // 기본 30초 + 대사당 10초 + 이미지당 5초
    const baseTime = 30;
    const scriptTime = scriptCount * 10;
    const imageTime = projectData.images.length * 5;

    return Math.max(60, baseTime + scriptTime + imageTime);
  };

  // 폰트 목록 가져오기
  useEffect(() => {
    const fetchFonts = async () => {
      try {
        setLoadingFonts(true);
        const response = await fetch('/font-list');
        const data = await response.json();

        if (data.status === 'success') {
          setAvailableFonts(data.data);
        } else {
          console.error('폰트 목록 가져오기 실패:', data.message);
        }
      } catch (error) {
        console.error('폰트 목록 가져오기 오류:', error);
      } finally {
        setLoadingFonts(false);
      }
    };

    fetchFonts();
  }, []);

  // 미리보기 생성
  const generatePreview = React.useCallback(async () => {
    if (!displayContent.title || !displayContent.body1) {
      alert('미리보기를 위해서는 제목과 첫 번째 대사가 필요합니다.');
      return;
    }

    setLoadingPreview(true);
    try {
      const response = await apiService.generatePreview({
        title: displayContent.title,
        body1: displayContent.body1,
        textPosition: textPosition,
        textStyle: textStyle,
        titleAreaMode: titleAreaMode,
        titleFont: titleFont,
        bodyFont: bodyFont,
        titleFontSize: titleFontSize,
        bodyFontSize: bodyFontSize,
        image: projectData.images[0] || undefined,
        jobId: projectData.jobId,  // Job ID 추가
      });

      if (response.status === 'success') {
        setPreviewImage(response.preview_url);
        console.log('미리보기 생성 성공:', response.message);
      } else {
        throw new Error(response.message || '미리보기 생성 실패');
      }

    } catch (error) {
      console.error('미리보기 생성 오류:', error);
      alert('미리보기 생성 중 오류가 발생했습니다.');
      setPreviewImage(null);
    } finally {
      setLoadingPreview(false);
    }
  }, [
    displayContent.title,
    displayContent.body1,
    projectData.images,
    projectData.jobId,
    textPosition,
    textStyle,
    titleAreaMode,
    titleFont,
    bodyFont,
    titleFontSize,
    bodyFontSize
  ]);

  // 자동 미리보기 생성
  useEffect(() => {
    // 필수 조건 확인: 제목, 첫 번째 대사, 이미지가 있을 때만 자동 생성
    // 폰트 로딩이 완료된 후에만 실행
    if (displayContent.title &&
        displayContent.body1 &&
        projectData.images.length > 0 &&
        !loadingFonts &&
        availableFonts.length > 0) {

      // 약간의 지연을 두어 컴포넌트가 완전히 마운트된 후 실행
      const timer = setTimeout(() => {
        generatePreview();
      }, 500);

      return () => clearTimeout(timer);
    }
  }, [
    projectData.content.title,
    projectData.content.body1,
    projectData.images,
    loadingFonts,
    availableFonts,
    generatePreview  // useCallback으로 메모이제이션된 함수 사용
  ]);

  // 배치 영상 생성 시작 (비동기)
  const startAsyncGeneration = async () => {
    // 이메일 주소 검증
    if (!userEmail.trim()) {
      setError('이메일 주소를 입력해주세요.');
      return;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(userEmail)) {
      setError('유효한 이메일 주소를 입력해주세요.');
      return;
    }

    setGenerationStatus('generating');
    setProgress(30);
    setError(null);
    setVideoPath(null);
    setJobId(null);
    setAsyncResponse(null);

    try {
      setStatusMessage('배치 작업 요청 중...');

      // ✅ ImageStep에서 수정된 데이터 수집
      const { editedTexts } = imageStepRef.current?.getEditedData() || { editedTexts: {} };

      // 배치 작업 API 호출
      const contentData = JSON.stringify(projectData.content);
      const editedTextsData = JSON.stringify(editedTexts);
      const response = await apiService.generateVideoAsync({
        userEmail: userEmail,
        content: contentData,
        images: projectData.images,
        imageUploadMode: projectData.imageUploadMode,
        textPosition: textPosition,
        textStyle: textStyle,
        titleAreaMode: titleAreaMode,
        musicFile: projectData.selectedMusic || undefined,
        musicMood: projectData.musicMood,
        titleFont: titleFont,
        bodyFont: bodyFont,
        titleFontSize: titleFontSize,
        bodyFontSize: bodyFontSize,
        voiceNarration: voiceNarration,
        crossDissolve: crossDissolve,
        subtitleDuration: subtitleDuration,
        jobId: projectData.jobId,  // Job ID 추가
        editedTexts: editedTextsData, // 수정된 텍스트 전달
      });

      if (response.status === 'success') {
        setProgress(50);
        setStatusMessage('작업이 큐에 추가되었습니다. 완료되면 이메일로 알려드립니다.');
        setJobId(response.job_id || null);
        setAsyncResponse(response);
        setEstimatedTime(response.estimated_time || '약 3-10분');
        setGenerationStatus('completed');
      } else {
        throw new Error(response.message || '작업 요청에 실패했습니다');
      }
    } catch (err: any) {
      console.error('배치 작업 요청 실패:', err);
      setError(err.message || '작업 요청 중 오류가 발생했습니다');
      setGenerationStatus('error');
      setProgress(0);
      setStatusMessage('');
    }
  };

  // 기존 동기식 영상 생성 (레거시)
  const startSyncGeneration = async () => {
    setGenerationStatus('generating');
    setProgress(0);
    setError(null);
    setVideoPath(null);

    try {
      // 진행률 시뮬레이션
      const progressSteps = [
        { progress: 10, message: '텍스트 분석 중...' },
        { progress: 20, message: 'TTS 음성 생성 중...' },
        { progress: 40, message: '이미지 처리 중...' },
        { progress: 60, message: '배경음악 준비 중...' },
        { progress: 80, message: '영상 합성 중...' },
        { progress: 95, message: '최종 렌더링 중...' },
      ];

      // 단계별 진행률 업데이트
      for (const step of progressSteps) {
        await new Promise(resolve => setTimeout(resolve, 2000));
        setProgress(step.progress);
        setStatusMessage(step.message);
      }

      // API 호출
      const contentData = JSON.stringify(projectData.content);
      const response = await apiService.generateVideo({
        content: contentData,
        images: projectData.images,
        imageUploadMode: projectData.imageUploadMode,
        textPosition: textPosition,
        textStyle: textStyle,
        titleAreaMode: titleAreaMode,
        musicFile: projectData.selectedMusic || undefined,
        musicMood: projectData.musicMood,
        titleFont: titleFont,
        bodyFont: bodyFont,
        voiceNarration: voiceNarration,
        crossDissolve: crossDissolve,
        subtitleDuration: subtitleDuration,
      });

      if (response.status === 'success') {
        setProgress(100);
        setStatusMessage('영상 생성 완료!');
        setVideoPath(response.video_path || null);
        setGenerationStatus('completed');
      } else {
        throw new Error(response.message || '영상 생성에 실패했습니다');
      }
    } catch (err: any) {
      console.error('영상 생성 실패:', err);
      setError(err.message || '영상 생성 중 오류가 발생했습니다');
      setGenerationStatus('error');
      setProgress(0);
      setStatusMessage('');
    }
  };

  // 영상 생성 시작 (모드에 따라 분기)
  const startGeneration = () => {
    if (enableAsyncMode) {
      startAsyncGeneration();
    } else {
      startSyncGeneration();
    }
  };

  // 영상 다운로드
  const downloadVideo = () => {
    if (videoPath) {
      const downloadUrl = apiService.getVideoDownloadUrl(videoPath);
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.download = `reels_${new Date().toISOString().slice(0, 10)}.mp4`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  // 새 프로젝트 시작 확인
  const handleNewProject = () => {
    setShowConfirmDialog(true);
  };

  const confirmNewProject = () => {
    setShowConfirmDialog(false);
    onReset();
  };

  // 프로젝트 요약 정보
  const getProjectSummary = () => {
    const scriptCount = Object.values(projectData.content)
      .slice(1)
      .filter(script => script?.trim()).length;

    let totalChars = 0;
    Object.values(projectData.content).slice(1).forEach(script => {
      if (script?.trim()) {
        totalChars += script.length;
      }
    });

    const estimatedDuration = Math.max(5, Math.ceil((totalChars / 450) * 60));

    return {
      scriptCount,
      totalChars,
      estimatedDuration,
      imageCount: projectData.images.length,
      imageMode: projectData.imageUploadMode === 'per-script'
        ? '대사마다 이미지'
        : projectData.imageUploadMode === 'per-two-scripts'
          ? '2대사마다 이미지'
          : '모든 대사에 미디어 1개',
      titleAreaMode: titleAreaMode === 'keep' ? '확보 (타이틀 + 미디어 영역)' : '제거 (전체 미디어)',
      textPosition: titleAreaMode === 'keep'
        ? (textPosition === 'top' ? '상단 (340-520px 영역)'
          : textPosition === 'bottom-edge' ? '최하단 (바닥에서 20px)'
          : '하단 (520-700px 영역)')
        : '미디어 위 오버레이',
      textStyle: projectData.textStyle === 'outline' ? '외곽선 (배경 투명)' : '반투명 검은 배경',
      musicName: projectData.selectedMusic?.displayName || '기본 음악',
      musicMood: projectData.musicMood,
    };
  };

  const summary = getProjectSummary();

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        영상 생성
      </Typography>
      <Typography variant="body1" color="text.secondary" sx={{ mb: 3 }}>
