                </Box>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                  {summary.scriptCount}개 대사 ({summary.totalChars}자) • 예상 길이: 약 {summary.estimatedDuration}초
                </Typography>
                <Box sx={{
                  mt: 1,
                  p: 1,
                  bgcolor: 'grey.50',
                  borderRadius: 1,
                  border: '1px solid',
                  borderColor: 'grey.200',
                  maxHeight: 120,
                  overflow: 'auto'
                }}>
                  <Typography variant="caption" component="pre" sx={{
                    fontFamily: 'monospace',
                    whiteSpace: 'pre-wrap',
                    wordBreak: 'break-word',
                    fontSize: '0.7rem',
                    lineHeight: 1.3
                  }}>
                    {JSON.stringify(
                      Object.fromEntries(
                        Object.entries(projectData.content).filter(([key, value]) =>
                          key === 'title' || (typeof value === 'string' && value.trim() !== '')
                        )
                      ),
                      null,
                      2
                    )}
                  </Typography>
                </Box>
              </CardContent>
            </Card>

            {/* 이미지 및 텍스트 정보 */}
            <Card sx={{ mb: 2 }}>
              <CardContent>
                <Box display="flex" alignItems="center" sx={{ mb: 1 }}>
                  <ImageIcon color="primary" sx={{ mr: 1 }} />
                  <Typography variant="subtitle2">미디어 & 텍스트 설정</Typography>
                </Box>
                <Typography variant="body2" color="text.secondary">
