                <LinearProgress 
                  variant="determinate" 
                  value={progress} 
                  sx={{ mb: 2, height: 8, borderRadius: 4 }}
                />
                <Typography variant="body2" color="text.secondary">
                  {progress}% 완료 (예상 시간: {Math.ceil((100 - progress) / 100 * estimatedTime / 60)}분 남음)
                </Typography>
                <Alert severity="warning" sx={{ mt: 2 }}>
                  생성 중에는 페이지를 닫지 마세요
                </Alert>
              </Box>
            )}

            {generationStatus === 'completed' && (
              <Box sx={{ textAlign: 'center', py: 4 }}>
                {enableAsyncMode ? (
                  <>
                    <Send sx={{ fontSize: 64, color: 'success.main', mb: 2 }} />
                    <Typography variant="h6" gutterBottom>
                      작업 요청 완료!
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                      배치 작업이 시작되었습니다
                    </Typography>
                    <Alert severity="success" sx={{ mb: 2 }}>
                      {statusMessage}
                    </Alert>

                    {asyncResponse && (
                      <Card sx={{ mb: 3, textAlign: 'left' }}>
                        <CardContent>
                          <Typography variant="subtitle2" gutterBottom>
                            📧 작업 정보
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            • 작업 ID: {jobId}
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            • 이메일: {asyncResponse.user_email}
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            • 예상 시간: {asyncResponse.estimated_time}
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            • 상태: 작업 큐에서 대기 중
                          </Typography>
                        </CardContent>
                      </Card>
                    )}

                    <Alert severity="info" sx={{ mb: 3, textAlign: 'left' }}>
                      <strong>다음 단계:</strong><br/>
                      1. 작업이 백그라운드에서 진행됩니다<br/>
                      2. 완료되면 입력하신 이메일로 다운로드 링크를 보내드립니다<br/>
                      3. 이메일의 링크를 클릭하여 영상을 다운로드하세요<br/>
                      4. 다운로드 링크는 48시간 후 만료됩니다
                    </Alert>
                  </>
                ) : (
                  <>
                    <CheckCircle sx={{ fontSize: 64, color: 'success.main', mb: 2 }} />
                    <Typography variant="h6" gutterBottom>
                      영상 생성 완료!
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                      릴스 영상이 성공적으로 생성되었습니다
                    </Typography>
                    <Alert severity="success" sx={{ mb: 3 }}>
                      {statusMessage}
                    </Alert>
                    <Button
                      variant="contained"
                      size="large"
                      startIcon={<Download />}
                      onClick={downloadVideo}
                      sx={{ mr: 2 }}
                    >
                      영상 다운로드
                    </Button>
                  </>
                )}

                <Button
                  variant="outlined"
                  size="large"
                  startIcon={<Refresh />}
                  onClick={handleNewProject}
                >
                  새 프로젝트
                </Button>
              </Box>
            )}

            {generationStatus === 'error' && (
              <Box sx={{ textAlign: 'center', py: 4 }}>
                <ErrorIcon sx={{ fontSize: 64, color: 'error.main', mb: 2 }} />
                <Typography variant="h6" gutterBottom>
                  생성 실패
                </Typography>
                <Alert severity="error" sx={{ mb: 3 }}>
                  {error}
                </Alert>
                <Button
                  variant="contained"
                  color="error"
                  size="large"
                  startIcon={<Refresh />}
                  onClick={startGeneration}
                  sx={{ mr: 2 }}
                >
                  다시 시도
                </Button>
                <Button
                  variant="outlined"
                  size="large"
                  onClick={onBack}
                >
                  설정 수정
                </Button>
              </Box>
            )}
          </Paper>
        </Grid>
      </Grid>

      {/* 하단 버튼 */}
      {generationStatus === 'idle' && (
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
          <Button
            variant="outlined"
            onClick={onBack}
            size="large"
          >
            이전: 음악 선택
          </Button>
        </Box>
      )}

      {/* 새 프로젝트 확인 다이얼로그 */}
      <Dialog open={showConfirmDialog} onClose={() => setShowConfirmDialog(false)}>
        <DialogTitle>새 프로젝트 시작</DialogTitle>
        <DialogContent>
          <Typography variant="body1">
            새 프로젝트를 시작하면 현재 작업 내용이 모두 초기화됩니다. 
            계속하시겠습니까?
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowConfirmDialog(false)}>취소</Button>
          <Button onClick={confirmNewProject} variant="contained" color="primary">
            확인
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default GenerateStep;
