import React, { useState, useRef } from 'react';
import {
  Box,
  Container,
  Stepper,
  Step,
  StepLabel,
  Typography,
  AppBar,
  Toolbar,
  Button,
  Avatar,
  IconButton,
  Menu,
  MenuItem,
} from '@mui/material';
import { AccountCircle, ExitToApp } from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import ContentStep from '../components/ContentStep';
import ImageStep, { ImageStepRef } from '../components/ImageStep';
import MusicStep from '../components/MusicStep';
import GenerateStep from '../components/GenerateStep';
import { ProjectData, ReelsContent, MusicMood, ImageUploadMode, MusicFile, TextPosition, TextStyle, TitleAreaMode, CrossDissolve } from '../types';
import * as apiService from '../services/api';

// UUID ÏÉùÏÑ± Ïú†Ìã∏Î¶¨Ìã∞ Ìï®Ïàò
const generateJobId = (): string => {
  return 'job_' + Math.random().toString(36).substr(2, 9) + '_' + Date.now().toString(36);
};

const steps = [
  'Î¶¥Ïä§ ÎåÄÎ≥∏ ÏûëÏÑ±',
  'Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú',
  'ÏùåÏïÖ ÏÑ†ÌÉù',
  'ÏòÅÏÉÅ ÏÉùÏÑ±',
];

const MainApp: React.FC = () => {
  const { user, logout } = useAuth();
  const [activeStep, setActiveStep] = useState(0);
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);

  // ‚úÖ ImageStepÏùò ref ÏÉùÏÑ± (ÏòÅÏÉÅ ÏÉùÏÑ± Ïãú Îç∞Ïù¥ÌÑ∞ ÏàòÏßëÏö©)
  const imageStepRef = useRef<ImageStepRef>(null);

  // ÌîÑÎ°úÏ†ùÌä∏ Îç∞Ïù¥ÌÑ∞ ÏÉÅÌÉú
  const [projectData, setProjectData] = useState<ProjectData>({
    jobId: generateJobId(), // Ï¥àÍ∏∞ Job ID ÏÉùÏÑ±
    content: {
      title: '',
      body1: '',
      body2: '',
      body3: '',
      body4: '',
      body5: '',
      body6: '',
      body7: '',
      body8: '',
    },
    images: [],
    imageUploadMode: 'per-two-scripts',
    textPosition: 'bottom',
    textStyle: 'outline',
    titleAreaMode: 'keep',
    selectedMusic: null,
    musicMood: 'bright',
    fontSettings: {
      titleFont: 'BMYEONSUNG_otf.otf',
      bodyFont: 'BMYEONSUNG_otf.otf',
      titleFontSize: 42,
      bodyFontSize: 36,
    },
    voiceNarration: 'enabled',
    crossDissolve: 'enabled',
    imagePanningOptions: {}, // üé® Ìå®Îãù ÏòµÏÖò Ï¥àÍ∏∞Ìôî
  });

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
    setProjectData({
      jobId: generateJobId(), // ÏÉàÎ°úÏö¥ Job ID ÏÉùÏÑ±
      content: {
        title: '',
        body1: '',
        body2: '',
        body3: '',
        body4: '',
        body5: '',
        body6: '',
        body7: '',
        body8: '',
      },
      images: [],
      imageUploadMode: 'per-two-scripts',
      textPosition: 'bottom',
      textStyle: 'outline',
      titleAreaMode: 'keep',
      selectedMusic: null,
      musicMood: 'bright',
      fontSettings: {
        titleFont: 'BMYEONSUNG_otf.otf',
        bodyFont: 'BMYEONSUNG_otf.otf',
        titleFontSize: 42,
        bodyFontSize: 36,
      },
      voiceNarration: 'enabled',
      crossDissolve: 'enabled',
    });
  };

  const handleContentChange = (content: ReelsContent) => {
    setProjectData(prev => ({ ...prev, content }));
  };

  const handleImagesChange = (images: File[], mode: ImageUploadMode) => {
    setProjectData(prev => ({
      ...prev,
      images,
      imageUploadMode: mode
    }));
  };

  // üé® Ìå®Îãù ÏòµÏÖò Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handlePanningOptionsChange = (options: { [key: number]: boolean }) => {
    setProjectData(prev => ({
      ...prev,
      imagePanningOptions: options
    }));
  };

  // ‚úÖ editedTextsÎäî ImageStepÏùò refÎ•º ÌÜµÌï¥ Í∞ÄÏ†∏Ïò§ÎØÄÎ°ú Î∂àÌïÑÏöî

  const handleMusicChange = (selectedMusic: MusicFile | null, musicMood: MusicMood) => {
    setProjectData(prev => ({
      ...prev,
      selectedMusic,
      musicMood
    }));
  };

  // ContentStepÏóêÏÑú "Îã§Ïùå" Î≤ÑÌäºÏùÑ ÎàÑÎ•º Îïå job Ìè¥Îçî ÏÉùÏÑ±
  const handleContentStepNext = async () => {
    try {
      console.log('üöÄ Job Ìè¥Îçî ÏÉùÏÑ± Ï§ë:', projectData.jobId);

      // BackendÏóê job Ìè¥Îçî ÏÉùÏÑ± ÏöîÏ≤≠
      await apiService.createJobFolder(projectData.jobId);

      console.log('‚úÖ Job Ìè¥Îçî ÏÉùÏÑ± ÏôÑÎ£å:', projectData.jobId);

      // Îã§Ïùå Îã®Í≥ÑÎ°ú Ïù¥Îèô
      handleNext();
    } catch (error) {
      console.error('‚ùå Job Ìè¥Îçî ÏÉùÏÑ± Ïã§Ìå®:', error);

      // Job IDÎ•º ÏÉàÎ°ú ÏÉùÏÑ±ÌïòÍ≥† Ïû¨ÏãúÎèÑ ÎòêÎäî ÏÇ¨Ïö©ÏûêÏóêÍ≤å ÏïåÎ¶º
      alert('ÏûëÏóÖ Ìè¥Îçî ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  // ImageStepÏóêÏÑú "Îã§Ïùå" Î≤ÑÌäºÏùÑ ÎàÑÎ•º Îïå ÏàòÏ†ïÎêú ÌÖçÏä§Ìä∏Î•º projectDataÏóê Î∞òÏòÅ
  const handleImageStepNext = () => {
    // ImageStep refÏóêÏÑú ÏàòÏ†ïÎêú ÌÖçÏä§Ìä∏ ÏàòÏßë
    const { editedTexts } = imageStepRef.current?.getEditedData() || { editedTexts: {} };

    // ÏõêÎ≥∏ content Î≥µÏÇ¨
    const updatedContent = { ...projectData.content };

    // editedTextsÎ•º contentÏóê Î≥ëÌï©
    Object.keys(editedTexts).forEach(imageIndexStr => {
      const imageIndex = parseInt(imageIndexStr);
      const texts = editedTexts[imageIndex];

      if (texts && texts.length > 0) {
        if (projectData.imageUploadMode === 'per-two-scripts') {
          // per-two-scripts: imageIndex 0 ‚Üí body1, body2
          const startIdx = imageIndex * 2 + 1;
          texts.forEach((text, idx) => {
            const bodyKey = `body${startIdx + idx}` as keyof ReelsContent;
            if (text && bodyKey in updatedContent) {
              updatedContent[bodyKey] = text;
            }
          });
        } else if (projectData.imageUploadMode === 'per-script') {
          // per-script: imageIndex 0 ‚Üí body1
          const bodyKey = `body${imageIndex + 1}` as keyof ReelsContent;
          if (texts[0] && bodyKey in updatedContent) {
            updatedContent[bodyKey] = texts[0];
          }
        }
      }
    });

    console.log('‚úÖ ÏàòÏ†ïÎêú ÌÖçÏä§Ìä∏ ÏÑ∏ÏÖòÏóê Î∞òÏòÅ:', updatedContent);

    // projectData ÏóÖÎç∞Ïù¥Ìä∏
    setProjectData(prev => ({ ...prev, content: updatedContent }));

    // Îã§Ïùå Îã®Í≥ÑÎ°ú Ïù¥Îèô
    handleNext();
  };


  const handleMenuClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    handleMenuClose();
    logout();
  };

  const getStepContent = (step: number) => {
    switch (step) {
      case 0:
        return (
          <ContentStep
            content={projectData.content}
            onChange={handleContentChange}
            onNext={handleContentStepNext} // Job Ìè¥Îçî ÏÉùÏÑ±ÏùÑ Ìè¨Ìï®Ìïú Ìï∏Îì§Îü¨ ÏÇ¨Ïö©
          />
        );
      case 1:
        return (
          <ImageStep
            ref={imageStepRef}
            images={projectData.images}
            imageUploadMode={projectData.imageUploadMode}
            content={projectData.content}
            jobId={projectData.jobId}
            onChange={handleImagesChange}
            onNext={handleImageStepNext}
            onBack={handleBack}
          />
        );
      case 2:
        return (
          <MusicStep
            selectedMusic={projectData.selectedMusic}
            musicMood={projectData.musicMood}
            onChange={handleMusicChange}
            onNext={handleNext}
            onBack={handleBack}
          />
        );
      case 3:
        return (
          <GenerateStep
            projectData={projectData}
            imageStepRef={imageStepRef}
            onBack={handleBack}
            onReset={handleReset}
          />
        );
      default:
        return null;
    }
  };

  return (
    <Box>
      {/* ÏÉÅÎã® Ìó§Îçî */}
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Î¶¥Ïä§ ÏòÅÏÉÅ ÏÉùÏÑ±Í∏∞
